import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  TextField,
  Button,
  CircularProgress,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  createUser,
  updateUser,
  selectUserById,
  selectUserStatus,
} from "../../redux/user/userSlice";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export const UserForm = () => {
  const { userId } = useParams();
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();
  const userStatus = useSelector(selectUserStatus);
  const selectedUser = useSelector((state) => selectUserById(state, userId));

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [skillsets, setSkillsets] = useState([]);
  const [hobby, setHobby] = useState("");

  useEffect(() => {
    if (selectedUser) {
      setUsername(selectedUser.username);
      setEmail(selectedUser.email);
      setPhoneNumber(selectedUser.phoneNumber);
      setSkillsets(selectedUser.skillsets);
      setHobby(selectedUser.hobby);
    }
  }, [selectedUser]);

  const handleUsernameChange = (e) => setUsername(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handlePhoneNumberChange = (e) => setPhoneNumber(e.target.value);
  const handleSkillsetsChange = (e) => setSkillsets(e.target.value);
  const handleHobbyChange = (e) => setHobby(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = { username, email, phoneNumber, skillsets, hobby };

    if (userId) {
      newUser._id = userId;
      dispatch(updateUser(newUser));
    } else {
      dispatch(createUser(newUser));
    }

    history.push("/users");
  };
  let formTitle;
  if (userId) {
    formTitle = "Edit User";
  } else {
    formTitle = "Create User";
  }

  let content;
  if (userStatus === "loading") {
    content = <CircularProgress />;
  } else {
    content = (
      <form onSubmit={handleSubmit}>
        <Typography variant="h6">{formTitle}</Typography>
        <TextField
          required
          fullWidth
          margin="normal"
          label="Name"
          value={username}
          onChange={handleUsernameChange}
        />
        <TextField
          required
          fullWidth
          margin="normal"
          label="Email"
          value={email}
          onChange={handleEmailChange}
        />
        <TextField
          required
          fullWidth
          margin="normal"
          label="Phone Number"
          value={phoneNumber}
          onChange={handlePhoneNumberChange}
        />
        <FormControl className={classes.formControl}>
          <InputLabel>Skillsets</InputLabel>
          <Select
            multiple
            value={skillsets}
            onChange={handleSkillsetsChange}
            renderValue={(selected) => selected.join(", ")}
          >
            <MenuItem value="HTML">HTML</MenuItem>
            <MenuItem value="CSS">CSS</MenuItem>
            <MenuItem value="JavaScript">JavaScript</MenuItem>
            <MenuItem value="React">React</MenuItem>
            <MenuItem value="Node.js">Node.js</MenuItem>
            <MenuItem value="MongoDB">MongoDB</MenuItem>
          </Select>
        </FormControl>
        <TextField
          required
          fullWidth
          margin="normal"
          label="Hobby"
          value={hobby}
          onChange={handleHobbyChange}
        />
        <Button type="submit" variant="contained" color="primary">
          Save
        </Button>
      </form>
    );
  }

  return <>{content}</>;
};
